mapper.select:
	 
 		select title, image, blog_id as blogId
	  	  from blog
		 where blog_id=#{blogId }

choose.when:

	update blog
	   set title = #{title}
	 where blog_id=#{blogId }

choose.otherwise:

	update blog
	   set title = #{title},
		   image = #{image}
	 where blog_id=#{blogId }
