mapper.select:

	    select count(b.no) as postCount, a.no, a.name, a.description, a.blog_id as blogId
	      from category a
	 left join post b
	        on a.no = b.category_no
	     where blog_id = #{blogId }
	  group by a.no;


	select no
	  from category
	 where blog_id = #{blogId }
	   and name = #{name}

mapper.delete:

	delete
	  from category
	 where no = #{no}
	   and blog_id = #{blogId}
